mapper.select:
select * from sensitive_words


        and id = #{entity.id}


        and words = #{entity.words}


        and type = #{entity.type}


        and is_deleted = #{entity.isDeleted}


limit #{start}, #{size}

select count(1)
from sensitive_words


        and id = #{id}


        and words = #{words}


        and type = #{type}


        and is_deleted = #{isDeleted}

mapper.update:
update sensitive_words
set is_deleted = 1
where id = #{id}
