mapper.select:
select * from share_circle


        and id = #{entity.id}


        and parent_id = #{entity.parentId}


        and circle_name = #{entity.circleName}


        and icon = #{entity.icon}


        and created_by = #{entity.createdBy}


        and created_time = #{entity.createdTime}


        and update_by = #{entity.updateBy}


        and update_time = #{entity.updateTime}


        and is_deleted = #{entity.isDeleted}


limit #{start}, #{size}

select count(1)
from share_circle


        and id = #{id}


        and parent_id = #{parentId}


        and circle_name = #{circleName}


        and icon = #{icon}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

mapper.update:
update share_circle
set is_deleted = 1
where id = #{id}
