mapper.select:
select * from share_message


        and id = #{entity.id}


        and from_id = #{entity.fromId}


        and to_id = #{entity.toId}


        and content = #{entity.content}


        and is_read = #{entity.isRead}


        and created_by = #{entity.createdBy}


        and created_time = #{entity.createdTime}


        and update_by = #{entity.updateBy}


        and update_time = #{entity.updateTime}


        and is_deleted = #{entity.isDeleted}


limit #{start}, #{size}

select count(1)
from share_message


        and id = #{id}


        and from_id = #{fromId}


        and to_id = #{toId}


        and content = #{content}


        and is_read = #{isRead}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

mapper.update:
update share_message
set is_deleted = 1
where id = #{id}
