mapper.select:
select * from share_moment


        and id = #{entity.id}


        and circle_id = #{entity.circleId}


        and content = #{entity.content}


        and pic_urls = #{entity.picUrls}


        and reply_count = #{entity.replyCount}


        and created_by = #{entity.createdBy}


        and created_time = #{entity.createdTime}


        and update_by = #{entity.updateBy}


        and update_time = #{entity.updateTime}


        and is_deleted = #{entity.isDeleted}


limit #{start}, #{size}

select count(1)
from share_moment


        and id = #{id}


        and circle_id = #{circleId}


        and content = #{content}


        and pic_urls = #{picUrls}


        and reply_count = #{replyCount}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

mapper.update:
update share_moment
set is_deleted = 1
where id = #{id}
