mapper.select:
select * from practice_set


        and id = #{entity.id}


        and set_name = #{entity.setName}


        and set_type = #{entity.setType}


        and set_heat = #{entity.setHeat}


        and set_desc = #{entity.setDesc}


        and primary_category_id = #{entity.primaryCategoryId}


        and created_by = #{entity.createdBy}


        and created_time = #{entity.createdTime}


        and update_by = #{entity.updateBy}


        and update_time = #{entity.updateTime}


        and is_deleted = #{entity.isDeleted}


limit #{start}, #{size}

select count(1)
from practice_set


        and id = #{id}


        and set_name = #{setName}


        and set_type = #{setType}


        and set_heat = #{setHeat}


        and set_desc = #{setDesc}


        and primary_category_id = #{primaryCategoryId}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

mapper.update:
update practice_set
set is_deleted = 1
where id = #{id}

update practice_set
set set_heat = set_heat + 1
where id = #{id}
