mapper.select:
select * from subject_mapping


        and id = #{entity.id}


        and subject_id = #{entity.subjectId}


        and category_id = #{entity.categoryId}


        and label_id = #{entity.labelId}


        and created_by = #{entity.createdBy}


        and created_time = #{entity.createdTime}


        and update_by = #{entity.updateBy}


        and update_time = #{entity.updateTime}


        and is_deleted = #{entity.isDeleted}


limit #{start}, #{size}

select count(1)
from subject_mapping


        and id = #{id}


        and subject_id = #{subjectId}


        and category_id = #{categoryId}


        and label_id = #{labelId}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

mapper.update:
update subject_mapping
set is_deleted = 1
where id = #{id}

mapper.select:
select
distinct label_id
from subject_mapping
where
is_deleted = 0

    and id = #{id}


    and subject_id = #{subjectId}


    and category_id = #{categoryId}


    and label_id = #{labelId}


    and created_by = #{createdBy}


    and created_time = #{createdTime}


    and update_by = #{updateBy}


    and update_time = #{updateTime}

select distinct * from subject_mapping where subject_id in
#{subjectId}
and is_deleted = 0
